.g-tab-fader {
	position: relative;
	margin: 0 auto;
	/*max-width: 600px;*/
	max-width: none;
	width: 100%;
}
.g-tab-fader-inner {
	height: 0;
	padding-bottom: 100%;
	width: 100%;
}

@media all and (min-width: 600px) {

	.g-tab-fader-inner {
		padding-bottom: 66.66%;
	}
}

.g-photo-wrap {
	height: 100%;
	left: 0;
	position: absolute;
	top: 0;
	transform: translate3d(0,0,0);
	width: 100%;
}

.g-photo-wrap .desktop {
	display: block;
}

@media screen and (max-width: 600px) {
	.g-photo-wrap .desktop {
		display: none;
	}
}

.g-photo-wrap .mobile {
	display: none;
}

@media screen and (max-width: 600px) {
	.g-photo-wrap .mobile {
		display: block;
	}
}

.g-photo-wrap img {
	left: 0;
	height: 100%;
	object-fit: cover;
	opacity: 0;
	position: absolute;
	top: 0;
	transition: opacity 0.9s;
	transform: translate3d(0,0,0);
	width: 100%;
}
.g-timestamp,
.g-caption {
	box-sizing: border-box;
	color: #fff;
	font-family: "nyt-franklin", Arial, Helvetica, sans-serif;
	font-size: 16px;
	letter-spacing: 0.02em;
	opacity: 0;
	padding: 20px;
	position: absolute;
	text-shadow: 0px 0px 10px rgba(0,0,0,0.25), 1px 1px 1px rgba(0,0,0,0.35), -1px -1px 1px rgba(0,0,0,0.35);

	transition: opacity 0.35s ease-in-out;
	transition-delay: 0s;
	width: 100%;
	z-index: 99;
/*	-webkit-font-smoothing: antialiased;
	-moz-osx-font-smoothing: grayscale;*/
}

@media all and (min-width: 740px) {
 .g-timestamp,
	.g-caption {
 font-size: 18px;
 }
}

.g-timestamp {
	font-weight: 700;
	transition-delay: 0.35s;
	top: 0;
	-webkit-font-smoothing: antialiased;
	-moz-osx-font-smoothing: grayscale;
}
.g-caption {
	bottom: -5px;
	line-height: 1;
	transition-delay: 0.9s;
}
.g-caption p {
	display: inline;
	/*font-family: nyt-cheltenham, georgia, serif;*/
	font-size: 14px;
	line-height: 1.25;
	margin-bottom: 0.25em;
}
@media all and (min-width: 740px) {
	.g-caption p {
		font-size: 16px;
		line-height: 1.25;
		margin-bottom: 0.5em;
	}
}
.g-photo-wrap .g-credit {
	color: rgba(255,255,255, 0.75);
	display: inline-block;
	font-size: 11px;
}
@media all and (min-width: 740px) {
	.g-photo-wrap .g-credit {
		font-size: 12px;
	}
}

.g-photo-wrap.g-photo-active img {
	opacity: 1;
}
.g-photo-wrap.g-photo-active .g-timestamp {
	opacity: 1;
}
.g-photo-wrap.g-photo-active .g-caption {
	opacity: 1;
}

#g-hp-tabs .g-tab-fader:before {
	display: none;
}











